    } else {
      res.status(200).json(response);
    }
  } catch (error) {
    next(error);
  }
};

// DELETE UBICACIÓN BY ID
export const deleteUbicacion = async (req, res, next) => {
  try {
    const { id, idNegocioOk, idAlmacenOk, serie, ubicacionId } = req.params;
