//Inventarios
import { Router } from "express";
import config from "../../../config/config";
// Import Routes
import almacenesRoutes from "./almacen.routes";
const routerAPI = (app) => {
  const router = Router();
  const api = config.API_URL;
  app.use(api, router);
  // Routes
  router.use("/pwa5/almacenes", almacenesRoutes);
  // Return Router
  return router;
};
module.exports = routerAPI;

